import { Box, IconButton, usetheme } from "@mui/material";
import { useContext } from "react";
import { ColorModeContext, tokens } from "../../theme";
import InputBase from "@mui/material/InputBase";
/*https://mui.com/material-ui/material-icons/*/
import LightModeOutlinedIcon  from "@mui/icons-material/LightModeOutlined";
import DarkModeOutlinedIcon  from "@mui/icons-material/DarkModeOutlined";
import NotificationsOuttinedIcon  from "@mui/icons-material/NotificationsOutlined";
import SettingsOutlinedIcon  from "@mui/icons-material/SettingsOutlined";
import PersonOutlinedIcon  from "@mui/icons-material/PersonOutlined";
import SearchIcon  from "@mui/icons-material/Search";



const Topbar = () => {
    /*to have access to theme provided*/
    const theme = useTheme();
    const colors = tokens(theme.palette.mode);
    const colorMode = useContext(ColorModeContext);

    return (
    <Box display="flex" justifyContent="space-between" p={2}>
        { /*SearchBar*/ }
        <Box display="flex" backgroundColor={colors.Primary[400]} borderRRadius= "3px"> 
        <InputBase sx={{ ml: 2, flex: 1 }} placeholder="search" />
        <IconButton type="button" sx={{ P: 1}}><IconButton /></IconButton>
        </Box>

        { /*Icons*/ }
        <Box display="flex"></Box>
{/*to change between light to dark*/}
         <IconButton onClick={colorMode.toggleColorMode}>
            {theme.palette.mode === 'dark' ? (
                <DarkModeOutlinedIcon />

            ) :(
                <LightModeOutlinedIcon />
            )}

         </IconButton>

         <IconButton>
         </IconButton>

         <IconButton>
         </IconButton>

         <IconButton>
         </IconButton>


    </Box>);
};
export default Topbar;
